'use strict';let SocketService={};SocketService.init=(room,callback)=>{class Socket{constructor(room){this.room=room;this.socket=io();this.socket.on('connected',()=>this.socket.emit('join-room',this.room));}emit(event,data){this.socket.emit('msg',{room:this.room,event,data});}on(event,f){this.socket.on(event,f);}}const c=document.createElement('script');c.src='https://socketservice.herokuapp.com/socket.io/socket.io.js';c.addEventListener('load',e=>callback(new Socket(room)));document.body.appendChild(c)}
