'use strict';let SocketService={};SocketService.init=(r,callback)=>{class Socket{constructor(r){this.r=r;this.s=io('https://socketservice.herokuapp.com/');this.s.on('connected',()=>this.s.emit('join-room',this.r));}emit(e,data){this.s.emit('msg',{room:this.r,event:e,data});}on(e,f){this.s.on(e,f);}}const c=document.createElement('script');c.src='https://socketservice.herokuapp.com/socket.io/socket.io.js';c.addEventListener('load',e=>callback(new Socket(r)));document.body.appendChild(c)}
